mapper.update:
UPDATE TBL_REVIEW	  
SET TITLE = #{title, jdbcType=VARCHAR}
, CONTENT = #{content, jdbcType=VARCHAR}
, MOD_DATE = SYSDATE()
  WHERE idx = #{idx}


UPDATE TBL_REVIEW
SET COUNT = IFNULL(COUNT, 0)+1
WHERE idx = #{idx}

mapper.select:
SELECT A.idx , A.restaurant_no , A.title , A.content , A.writer_name , A.writer_id , A.reg_date , A.mod_date , A.count FROM tbl_review A WHERE 1=1
AND ( A.TITLE LIKE CONCAT('%', #{searchKeyword}, '%'))
ORDER BY A.idx DESC LIMIT #{firstIndex} #{recordCountPerPage}


SELECT USER_NAME
FROM TBL_USER
WHERE 1=1
AND USER_ID = #{userId}
AND USER_PW = #{userPw}
